import React from "react";


const AddMovie = () => {
  return (
    <div className="flex flex-col text-left p-1  ">
      <label>Title</label>
      <input className="border-2 rounded-lg my-2 p-1 px-2"  type="text" />
      <label>Opening Text</label>
      <textarea  className="border-2 px-2 my-2 rounded-lg"  cols="30" rows="10"></textarea>
      <label>Release Date</label>
      <input type="text"  className="border-2 my-2 rounded-lg"/>
      <button className="w-3/12 place-self-center mt-4">Add Movie</button>
    </div>
  );
};

export default AddMovie;
